pre_install:
if(!(Test-Path("$dir\FastCopy2.ini"))) {
    New-Item "$dir\FastCopy2.ini" -type file
    write-output "[main]" | Out-File -encoding "UTF8" $dir\FastCopy2.ini
}
if(!(Test-Path("$dir\FastCopy2.ini.bak"))) {
    New-Item "$dir\FastCopy2.ini.bak" -type file
    write-output "" | Out-File -encoding "UTF8" $dir\FastCopy2.ini.bak
}
if(!(Test-Path("$dir\FastCopy2.ini.original"))) {
    New-Item "$dir\FastCopy2.ini.original" -type file
    write-output "" | Out-File -encoding "UTF8" $dir\FastCopy2.ini.original
}
